mapper.select:
select dept_id,dept_name
from tb_dept
where state=#{state } and company_id=#{companyId }

mapper.update:
update tb_dept
set dept_name = #{deptName },
  english_name = #{englishName },
  tel = #{tel },
  dept_icon = #{deptIcon }
where dept_id = #{deptId }

update tb_dept
set state = #{state }
where dept_id = #{deptId }

