mapper.select:
select

from tb_sec_resource
where name = #{name}

and id != #{id}

select

from tb_sec_resource
where permission = #{permission}

and id != #{id}

select

from tb_sec_resource
where priority = #{priority} and parent_id = #{parentId}

and id != #{id}

select r.id, r.priority 
from tb_sec_resource r
where r.parent_id = #{parentId}
and r.priority < #{priority}
order by r.priority desc limit 1

select r.id, r.priority 
from tb_sec_resource r
where r.parent_id = #{parentId}
and r.priority > #{priority}
order by r.priority asc limit 1
