mapper.select:
select a.app_id,a.app_name from tb_user_app_right ua left join tb_app a on ua.app_id=a.app_id where ua.user_id=#{userId } and a.off_line=#{offLine }
and ua.auth_from = #{authFrom}
group by a.app_id order by create_date desc

  	select left(date_add(r.create_date ,INTERVAL -weekday(r.create_date ) day),10)time,count(r.report_id)as runNum from tb_report r 
	where r.flag =0 and r.user_id = ${userId}
	group by time order by time desc

SELECT app_id
FROM tb_user_app_right
WHERE user_id = #{userId}
