mapper.select:
select

from tb_file
where user_id = #{userId }
and another_name = #{anotherName
}
and state = #{state}
and file_id not in (select file_id from
tb_experiment where user_id =
#{userId })
order by create_date desc

select
,
from tb_file where data_key = #{dataKey}

select count(*) size from
tb_file where user_id = #{userId } and state =
#{state }

select sum(size) size from
tb_file where user_id = #{userId } and state =
#{state }

mapper.update:
update tb_file
set data_key=#{dataKey },size=#{size },path=#{path},

 another_name=#{anotherName },

state=#{state },md5=#{md5 },batch=#{batch }

	,file_format=#{fileFormat}

where file_id=#{fileId }

mapper.select:
select
,
from tb_file f left join tb_file_tag_relat filetag on f.file_id = filetag.file_id left join tb_app_tag_relat apptag on filetag.tag_id = apptag.tag_id where user_id = #{userId } and state = #{state } and apptag.app_id= #{appId }
and (data_key like '%${condition}%' or file_name like '%${condition}%' or another_name like '%${condition}%' or batch like '%${condition}%')
order by f.create_date ${sortDate},f.batch ${sortBatch},f.file_name ${sortName}
order by f.batch ${sortBatch},f.create_date ${sortDate},f.file_name ${sortName}
order by f.file_name ${sortName},f.batch ${sortBatch},f.create_date ${sortDate}

select
,
, (select count(*) reportNum from tb_report r where r.period = #{period} and r.flag = #{reportType } and r.state = #{state } and r.file_id = f.file_id) reportNum, (select count(*) isRunning from tb_report r where r.period != #{period } and r.flag = #{reportType } and r.state = #{state } and r.file_id=f.file_id) isRunning ,
from tb_file f left join tb_file_tag_relat filetag on f.file_id = filetag.file_id left join tb_tag tag on tag.tag_id = filetag.tag_id left join tb_app_tag_relat apptag on tag.tag_id = apptag.tag_id where user_id = #{userId } and state = #{state }
and (data_key like '%${condition}%' or file_name like '%${condition}%' or another_name like '%${condition}%' or data_tags like '%${condition}%' or strain like '%${condition}%' or sample like '%${condition}%' or batch like '%${condition}%')
order by f.create_date ${sortDateType},f.file_name ${sortNameType}
order by f.another_name ${sortAnotherName}, create_date desc
order by reportNum ${sortRun}, create_date desc
order by f.file_name ${sortNameType},f.create_date ${sortDateType}

select
,
, (select count(*) reportNum from tb_report r where r.period = #{period } and r.flag = #{reportType } and r.state = #{state } and r.file_id=f.file_id) reportNum, (select count(*) isRunning from tb_report r where r.period != #{period } and r.flag = #{reportType } and r.state = #{state } and r.file_id=f.file_id) isRunning ,
from tb_file f left join tb_file_tag_relat filetag on f.file_id = filetag.file_id left join tb_tag tag on tag.tag_id = filetag.tag_id left join tb_app_tag_relat apptag on tag.tag_id = apptag.tag_id where user_id = #{userId } and state = #{state }
and (data_key like '%${condition}%' or file_name like '%${condition}%' or another_name like '%${condition}%' or data_tags like '%${condition}%' or strain like '%${condition}%' or sample like '%${condition}%' or batch like '%${condition}%')
order by f.create_date ${sortDate},f.batch ${sortBatch},f.file_name ${sortName}
order by f.batch ${sortBatch},f.create_date ${sortDate},f.file_name ${sortName}
order by f.file_name ${sortName},f.batch ${sortBatch},f.create_date ${sortDate}

select

from tb_file
where file_name like '${fileName}%'
and batch = #{batch}
and state = #{state}
and user_id= #{userId}

select

,

,
(select count(*) reportNum from tb_report r where r.period = #{period
} and
r.flag = #{reportType } and r.state = #{state } and
r.file_id=f.file_id) reportNum,
(select count(*) isRunning from
tb_report r where r.period != #{period } and
r.flag = #{reportType }
and r.state = #{state } and
r.file_id=f.file_id) isRunning
from tb_file f

	user_id = #{userId } and state = #{state }
	
		
			and (file_name like concat('%',#{condition},'%')
			or sample like concat('%',#{condition},'%')
			or batch like concat('%',#{condition},'%'))
		
		
			and sample like concat('%',#{sample},'%')
		
	


	
		order by create_date ${sord}
	
	
		order by batch ${sord}
	
	
		order by file_name ${sord}
	
	
		order by size ${sord}
	
	
		order by sample ${sord}
	
	
		order by create_date ${sord}
