mapper.insert:
insert into tb_medicine (id, app_id, result_simple, result_detail, 
  advice, feature, remark
  )
values (#{id,jdbcType=INTEGER}, #{appId,jdbcType=INTEGER}, #{resultSimple,jdbcType=VARCHAR}, 
  #{resultDetail,jdbcType=VARCHAR}, #{advice,jdbcType=VARCHAR}, #{feature,jdbcType=VARCHAR}, #{remark,jdbcType=VARCHAR}
  )

mapper.select:
  	select

  	from tb_medicine m


		  	and m.feature = #{feature}


		  	and m.result_detail = #{resultDetail}


		  	and m.app_id = #{appId}
