mapper.insert:
insert into tb_medicine (id, app_id, result, 
  advice, feature, remark
  )
values (#{id,jdbcType=INTEGER}, #{appId,jdbcType=INTEGER}, #{result,jdbcType=VARCHAR}, 
  #{advice,jdbcType=VARCHAR}, #{feature,jdbcType=VARCHAR}, #{remark,jdbcType=VARCHAR}
  )

mapper.select:
  	select

  	from tb_medicine m


		  	and m.feature = #{feature}


		  	and m.result = #{result}


		  	and m.app_id = #{appId}
