mapper.insert:
insert into tb_metadata (id, app_id, flag, 
  name, seq, priority, 
  create_date, expire_date, state
  )
values (#{id,jdbcType=INTEGER}, #{appId,jdbcType=INTEGER}, #{flag,jdbcType=INTEGER}, 
  #{name,jdbcType=VARCHAR}, #{seq,jdbcType=VARCHAR}, #{priority,jdbcType=INTEGER}, 
  #{createDate,jdbcType=TIMESTAMP}, #{expireDate,jdbcType=TIMESTAMP}, #{state,jdbcType=INTEGER}
  )

mapper.select:
SELECT m.name as id, m.name as text
FROM tb_metadata m, tb_app_tag_relat atr, tb_app_metadata_relat amr
WHERE atr.tag_id = #{tagId}
AND amr.app_id = atr.app_id
AND amr.meta_id = m.id
AND m.flag = #{flag}

SELECT distinct m.name name, m.seq seq
FROM tb_metadata m
WHERE m.flag = #{flag}
