mapper.update:
    update tb_sample
    set is_add = #{isAdd,jdbcType=BIT},order_id = #{orderId,jdbcType=INTEGER}, update_date=now()
    where sample_id in(

	     #{sampleId,jdbcType=INTEGER}
    )

mapper.select:
SELECT s.sample_id sampleId, s.sample_name sampleName, s.type type, p.name name, p.gender gender,
p.age age, p.tel tel, p.id_card idCard, s.create_date createDate, t.tag_name tagName
FROM tb_sample s, tb_patient p, tb_sample_tag_relat str, tb_tag t
WHERE s.user_id = #{userId}
AND s.is_add = #{isAdd}
AND s.state = #{state}
AND s.patient_id = p.id
AND s.sample_id = str.sample_id
AND t.tag_id = str.tag_id

SELECT s.sample_id sampleId, s.sample_name sampleName, s.type type, p.name name, p.gender gender,
p.age age, p.tel tel, p.id_card idCard, s.create_date createDate, t.tag_name tagName, t.tag_id tagId
FROM tb_sample s, tb_patient p, tb_sample_tag_relat str, tb_tag t
WHERE s.user_id = #{userId}
AND s.is_add = #{isAdd}
AND s.state = #{state}
AND s.patient_id = p.id
AND s.sample_id = str.sample_id
AND t.tag_id = str.tag_id
AND s.sample_id = #{sampleId}

mapper.delete:
delete from tb_sample
where sample_id in(

     #{sampleId,jdbcType=INTEGER}
)

mapper.select:
select s.*,sl.create_date log_date from 
tb_sample s,tb_sample_log sl,tb_sample_order o
where s.sample_id=sl.sample_id and s.order_id=o.id 
and sl.exper_state=#{experState}
and s.state=#{state} and s.sample_name=#{sampleName,jdbcType=VARCHAR}
and s.is_add = #{isAdd,jdbcType=BIT}
and o.order_no=#{orderNo} 
order by create_date desc

select s.*,sl.create_date log_date from 
tb_sample s,tb_sample_log sl
where s.sample_id=sl.sample_id and sl.exper_state=#{experState} and s.state=#{state} and s.exper_sample_name=#{experSampleName,jdbcType=VARCHAR}
and s.is_add = #{isAdd,jdbcType=BIT}
order by create_date desc

select s.*
from tb_sample s,tb_sample_order so
where s.order_id = so.id and s.sample_name=#{sampleName} and so.order_no=#{orderNo} and s.state =#{state,jdbcType=BIT}

select

from tb_sample
where state=#{state} and exper_sample_name=#{experSampleName}

select s.sample_id, s.user_id, s.order_id, s.sample_name,s.exper_sample_name, s.is_add, s.type, s.sindex, s.create_date, s.update_date, s.state, s.remark,t.tag_id,t.tag_name,sl.create_date as log_date, sl.create_date updateDate, sl.id sampleLogId,sl.exper_state,sr.order_no orderNo from tb_sample s left join tb_sample_log sl on s.sample_id=sl.sample_id left join tb_sample_tag_relat st on s.sample_id=st.sample_id left join tb_tag t on st.tag_id=t.tag_id left join tb_sample_order sr on s.order_id=sr.id where s.user_id = #{userId} and s.state =#{state,jdbcType=BIT} and sl.state=#{state,jdbcType=BIT}
and s.sample_name like '%${sampleName}%'
order by s.create_date desc,s.sample_name desc

