mapper.update:
    update tb_sample
    set is_add = #{isAdd,jdbcType=BIT}, update_date=#{updateDate, jdbcType=TIMESTAMP}
    where sample_id in(

	     #{sampleId,jdbcType=INTEGER}
    )

mapper.delete:
delete from tb_sample
where sample_id in(

     #{sampleId,jdbcType=INTEGER}
)

mapper.select:
    select s.*,sl.id sampleLogId from 
	tb_sample s,tb_sample_log sl
	where s.sample_id=sl.sample_id and sl.exper_state=#{experState} and sl.user_id=#{userId} and s.state=#{state} and sl.state=#{state}

select s.*,sl.create_date log_date from 
tb_sample s,tb_sample_log sl
where s.sample_id=sl.sample_id and sl.exper_state=#{experState} and sl.user_id=#{userId} and s.state=#{state} and s.sample_name=#{sampleName,jdbcType=VARCHAR}
and s.is_add = #{isAdd,jdbcType=BIT}
order by create_date desc
