mapper.select:
select count(command) runNum from tb_task where period=#{period}
and
app_id in(

	#{appId,jdbcType=INTEGER}

)
and state=#{state}

select f.file_name,f.file_id,f.batch,f.another_name,s.sample_name,t.*
from
(select * from tb_task where
	(task_id in (select max(task_id) from
	tb_task where state=#{state} and
	user_id=#{userId} and
	app_id=#{appId}
	group by data_key))
	or (data_key is null and state=#{state} and
	user_id=#{userId} and
	app_id=#{appId})
)t
left join tb_file f on f.data_key=t.data_key
left join tb_sample s on s.sample_id=t.sample_id

	
		
			(f.file_name like concat('%',#{condition},'%')
			or s.sample_name like concat('%',#{condition},'%')
			or f.batch like concat('%',#{condition},'%'))
		
		
			s.sample_name like concat('%',#{sample},'%')
		
	
	
		and t.period in
		
			#{period}
		
	
	
		and f.batch in
		
			#{batch}
		
	
	
		and t.update_date >= #{beginDate}
	
	
		and t.update_date <= #{endDate}
	


	
		order by update_date ${sord}
	
	
		order by f.batch ${sord}
	
	
		order by f.file_name ${sord}
	
	
		order by s.sample_name ${sord}
	
	
		order by t.update_date ${sord}

select f.file_name,f.file_id,f.batch,f.another_name,s.sample_name,t.* from (select * from tb_task where task_id in (select max(task_id) from tb_task where state=#{state} and ((user_id=#{userId} and (app_id=#{appId} or (app_id=113 and period!=2))) or (app_id=#{appId} and sample_id in (select sample_id from tb_sample where user_id=#{userId} and state=#{state}))) group by data_key) )t left join tb_file f on f.data_key=t.data_key left join tb_sample s on s.sample_id=t.sample_id where f.state=0
and (file_name like '%${condition}%' or f.batch like '%${condition}%' or s.sample_name like '%${condition}%')
and f.batch in (${batch})
and t.period in (${period})
and t.update_date >= '${beginDate} 00:00:00'
and t.update_date <= '${endDate} 23:59:59'
and s.sample_name like '%${sampleName}%'
order by t.update_date ${sortDate},f.batch ${sortBatch},f.file_name ${sortName},t.period ${sortPeriod}
order by f.batch ${sortBatch},t.update_date ${sortDate},f.file_name ${sortName},t.period ${sortPeriod}
order by f.file_name ${sortName},t.update_date ${sortDate},f.batch ${sortBatch},t.period ${sortPeriod}
order by t.period ${sortPeriod},t.update_date ${sortDate},f.batch ${sortBatch},f.file_name ${sortName}

select

from tb_task WHERE user_id=#{userId} and state=#{state} and period =
#{period} and params=#{params}

SELECT
t.task_id, t.user_id, t.app_id, t.data_key, t.command, t.period,
t.create_date, t.start_date, t.end_date, t.update_date, t.project_id,
t.delete_date, t.state, t.sample_id, t.datalist, t.result
FROM tb_task t
LEFT JOIN tb_file f
ON t.data_key = f.data_key
WHERE t.user_id = #{userId}
AND t.state=#{state}
AND t.period = #{period}
AND t.params=#{params}
AND f.batch = #{batch}

select

from tb_task WHERE data_key = #{dataKey} and state=#{state} and
app_id=#{appId}
and project_id=#{projectId}

	    select f.file_id,f.file_name,f.data_key,f.batch,s.sample_name,t.task_id,t.app_id,t.project_id,t.period,t.update_date,tag.tag_name,a.app_name, a.english_name
		from 
		(select * from tb_task where
          task_id in 
            (select max(task_id) from tb_task where state=#{state} and 
            (user_id=#{userId} or (sample_id in (select sample_id from tb_sample where user_id=#{userId} and state=#{state})))
             group by data_key)
        )t
		left join tb_file f on t.data_key=f.data_key
		left join tb_sample s on t.sample_id = s.sample_id
		left join tb_file_tag_relat ftr on ftr.file_id=f.file_id
		left join tb_tag tag on tag.tag_id = ftr.tag_id
		left join tb_app a on t.app_id = a.app_id
		where f.state=#{state} 
        
            and (f.file_name like concat('%',#{condition},'%')
            or s.sample_name like concat('%',#{condition},'%'))
        
        
            and t.period=#{period}
        
        
            and tag.tag_id=#{tagId}
        
        
            and f.batch=#{batch}
        
        
            and t.update_date>=#{beginDate}
        
        
            and t.update_date<=#{endDate}
        
        order by t.update_date ${sord}
