SELECT

FROM tb_user

    
        AND username = #{user.username}
    
    
        AND password = #{user.password}
    
    
        AND email = #{user.email}
    
    
        AND cellphone = #{user.cellphone}
    
    
        AND create_date = #{user.createDate}
    
    
        AND icon = #{user.icon}
    
    
        AND role = #{user.role}
    
    
        AND remark = #{user.remark}
    
    
        AND navigation = #{user.navigation}
    
    
        AND truename = #{user.truename}
    
    
        AND state = #{user.state}
    
    
        AND dept_id = #{user.deptId}
    
    
        AND login_uuid = #{user.loginUuid}
    
    
        AND company_id = #{user.companyId}
    
    
        AND sign = #{user.sign}
    
    
        AND balances = #{user.balances}
    
    AND user_id not in ${testAccountIds}

SELECT

FROM tb_user
WHERE company_id = #{companyId}
AND user_id not in (${testAccountIds})
AND user_id != #{loginUserId}
AND role = 0

SELECT role_id
FROM tb_sec_user_role_relat
