update tb_app set app_doc='<p style="text-indent:2em;">肝炎病毒是指引起病毒性肝炎的病原体。乙型病毒性肝炎是由乙肝病毒（hepatitis B virus，HBV）感染引起的，以肝脏炎性病变为主，并可引起多器官损害的一种疾病。我国属于感染的高发区，现有的慢性HBV感染者约9300万例。通过对血液样本中病毒DNA的扩增、测序和生物信息学检测分析，可快速获得病毒的基因型和SNPs位点信息，有助于早期诊断是否感染耐药突变株，治疗中辅助临床医生个性化用药治疗。</p><br><p>乙肝耐药突变位点统计表:</p><table class="table table-main -report"><thead><tr><th colspan="12">乙肝耐药突变检测，6种药物与20多种对应的耐药位点</th></tr></thead><tbody><tr><td colspan="2">RT区域密码子</td><td>169</td><td>173</td><td>180</td><td>181</td><td>184</td><td>194</td><td>202</td><td>204</td><td>236</td><td>250</td></tr><tr><td colspan="2">野生型</td><td>I</td><td>V</td><td>L</td><td>A</td><td>T</td><td>A</td><td>S</td><td>M</td><td>N</td><td>M</td></tr><tr><td colspan="2">乙肝药物名称</td><td colspan="10">耐药相关的位点突变</td></tr><tr><td>阿德福韦酯</td><td>Adefovir</td><td></td><td></td><td></td><td>V/T</td><td></td><td></td><td></td><td></td><td>T</td><td></td></tr><tr><td>拉米夫定</td><td>Lamivudine</td><td></td><td>L</td><td>M</td><td></td><td></td><td></td><td></td><td>I/V</td><td></td><td></td></tr><tr><td>替比夫定</td><td>Telbivudine</td><td></td><td></td><td></td><td></td><td></td><td></td><td></td><td>I</td><td></td><td></td></tr><tr><td>恩替卡韦</td><td>Entecavir</td><td>T</td><td></td><td>M</td><td></td><td>A/G/S/I/L/F</td><td></td><td>G/I</td><td>V</td><td></td><td>V/L/I</td></tr><tr><td>替诺福韦酯</td><td>Tenofovir</td><td></td><td></td><td></td><td></td><td></td><td>T</td><td></td><td></td><td></td><td></td></tr><tr><td>恩曲他滨</td><td>Emtricitabine</td><td></td><td>L</td><td>M</td><td></td><td></td><td></td><td></td><td>I/V</td><td></td><td></td></tr><tr><td colspan="12" style="text-align:left;">备注：若患者检测的密码子与野生型不一致，并造成氨基酸的改变，则说明位点发生基因突变，对应的药物产生耐药</td></tr></tbody></table>' where app_id=82;