mapper.select:
select count(DISTINCT app.app_id) 
from tb_app app 
where app.off_line=#{offLine}

  and app.company_id=#{companyId}

select sum(temp.data_num) runNum,temp.app_name app_name from (SELECT DISTINCT p.project_id,p.data_num,a.app_id,a.app_name from tb_app a left join tb_report r on a.app_id=r.app_id left join tb_project p on p.project_id=r.project_id where a.off_line=#{offLine}
and a.company_id=#{companyId}
and p.user_id not in(${testAccountIds})
and r.flag=#{flag} and r.period=#{period}) temp GROUP BY temp.app_id ORDER BY runNum desc limit 0,10;

select sum(temp.data_num) runNum,temp.username userName from (SELECT DISTINCT p.project_id,p.data_num,u.username,u.user_id from tb_app a left join tb_report r on a.app_id=r.app_id left join tb_project p on p.project_id=r.project_id left join tb_user u on u.user_id=p.user_id where a.off_line=#{offLine}
and a.company_id=#{companyId}
and p.user_id not in(${testAccountIds})
and r.flag=#{flag} and r.period=#{period}) temp GROUP BY temp.user_id ORDER BY runNum desc limit 0,10;

    select a.app_id,a.app_name,p.price,p.create_date charge_time
	from tb_app a
	left join (select * from tb_price where item_type=#{priceType} and expire_date is null) p on p.item_id=a.app_id
	where a.off_line=#{offLine}

    and a.company_id=#{companyId}

mapper.update:
update tb_app
set off_line = #{offLine,jdbcType=INTEGER}
where app_id = #{appId,jdbcType=INTEGER}

mapper.delete:
delete from 
tb_user_app_right
where user_id = #{userId}
and app_id in

#{app.appId}
