mapper.insert:
insert into tb_metadata (id, app_id, flag, 
  name, seq, priority, 
  create_date, expire_date, state
  )
values (#{id,jdbcType=INTEGER}, #{appId,jdbcType=INTEGER}, #{flag,jdbcType=INTEGER}, 
  #{name,jdbcType=VARCHAR}, #{seq,jdbcType=VARCHAR}, #{priority,jdbcType=INTEGER}, 
  #{createDate,jdbcType=TIMESTAMP}, #{expireDate,jdbcType=TIMESTAMP}, #{state,jdbcType=INTEGER}
  )

mapper.select:
select 

from tb_metadata
where app_id = #{meta.appId,jdbcType=INTEGER} and flag = #{meta.flag,jdbcType=INTEGER} and state = #{meta.state,jdbcType=INTEGER}
and priority = (
	select max(priority) from tb_metadata 
	where app_id = #{meta.appId,jdbcType=INTEGER} and flag = #{meta.flag,jdbcType=INTEGER} 
	and state = #{meta.state,jdbcType=INTEGER}
	and priority < #{meta.priority,jdbcType=INTEGER}
)

select 

from tb_metadata
where app_id = #{meta.appId,jdbcType=INTEGER} and flag = #{meta.flag,jdbcType=INTEGER} and state = #{meta.state,jdbcType=INTEGER}
and priority = (
	select min(priority) from tb_metadata 
	where app_id = #{meta.appId,jdbcType=INTEGER} and flag = #{meta.flag,jdbcType=INTEGER} 
	and state = #{meta.state,jdbcType=INTEGER}
	and priority > #{meta.priority,jdbcType=INTEGER}
)

select 

from tb_metadata
where app_id = #{meta.appId,jdbcType=INTEGER} and flag = #{meta.flag,jdbcType=INTEGER} and state = #{meta.state,jdbcType=INTEGER}
and 
(
	name = #{meta.name,jdbcType=VARCHAR}
	or 
	seq = #{meta.seq,jdbcType=VARCHAR}
)
