mapper.select:
select

from tb_user
where state = 0
AND  (
      username = #{username,jdbcType=VARCHAR}
      OR email = #{username,jdbcType=VARCHAR}
      )
AND password = #{password,jdbcType=VARCHAR}
AND (role=1 or role=2)

select count(DISTINCT ucr.user_id) 
from tb_user_company_relat ucr LEFT JOIN tb_user u on u.user_id=ucr.user_id 
where u.state=#{state}

and ucr.company_id=#{companyId}


and ucr.user_id not in(${testAccountIds})

select count(DISTINCT u.company_id) 
from tb_user_company_relat ucr 
LEFT JOIN tb_user u on u.user_id=ucr.user_id 
where u.state=#{state}

and ucr.company_id=#{companyId}


and ucr.user_id not in(${testAccountIds})

select count(DISTINCT u.company_id) `value`,c.province `name` from tb_user_company_relat ucr LEFT JOIN tb_user u on u.user_id=ucr.user_id LEFT JOIN tb_company c on u.company_id=c.company_id where u.state=#{state} and not ISNULL(c.province) and c.state=#{state}
and ucr.company_id=#{companyId}
and ucr.user_id not in(${testAccountIds})
GROUP BY c.province

select count(l.user_id) logNum, a.username user_name from tb_log l INNER JOIN ( select DISTINCT ucr.user_id,u.username from tb_user_company_relat ucr LEFT JOIN tb_user u on u.user_id=ucr.user_id where u.state=#{state}
and ucr.company_id=#{companyId}
and ucr.user_id not in(${testAccountIds}) ) a on l.user_id=a.user_id where l.operate='登录' or l.operate='用户登录' GROUP BY l.user_id ORDER BY logNum desc LIMIT 0,20

		SELECT t.time,n.userNum from (SELECT DISTINCT date_format(usr.create_date,'%Y-%m') time from tb_user usr ) t
        LEFT  JOIN (
        SELECT count(u.user_id) userNum,date_format(u.create_date,'%Y-%m') time from tb_user_company_relat ucr 
        LEFT JOIN tb_user u on u.user_id=ucr.user_id
        where u.state=#{state} 

        and ucr.company_id=#{companyId}


        and ucr.user_id not in(${testAccountIds})


        GROUP BY time
        ) n on t.time=n.time
        ORDER BY time;

select count(DISTINCT ucr.user_id) userNum,ucr.company_id companyId from tb_user_company_relat ucr LEFT JOIN tb_user u on u.user_id=ucr.user_id LEFT JOIN tb_company c on c.company_id=ucr.company_id where u.state=#{state} and c.state=#{state}
and ucr.user_id not in(${testAccountIds})
GROUP BY ucr.company_id

    select DISTINCT u.user_id,u.username from tb_user_company_relat ucr
	INNER JOIN tb_user u on ucr.user_id=u.user_id
	where u.state=#{state } 

    and ucr.company_id=#{companyId}


    and ucr.user_id not in(${testAccountIds})
